import React from "react";
import { useDispatch, useSelector } from "react-redux";

export const List = () => {
  const todos = useSelector((state) => {
    return state.todos;
  });

  const dispatch = useDispatch();
  return (
    <div>
      <div>
        <h2>TODOLIST</h2>
        {todos
          .filter((todo) => {
            todo.isDone === false;
          })
          .map((todo) => {
            return (
              <div
                style={{
                  border: "1px solid black",
                  margin: "10px",
                }}
                key={todo.id}
              >
                {todo.id} <br />
                {todo.title} <br />
                {todo.body} <br />
                {todo.isDone.toString()} <br />
                <button
                  onClick={() => {
                    dispatch({
                      type: "SWITCH_TODO",
                      payload: todo.id,
                    });
                  }}
                >
                  완료
                </button>
                <button
                  onClick={() => {
                    dispatch({
                      type: "DELETE_TODO",
                      payload: todo.id,
                    });
                  }}
                >
                  삭제
                </button>
              </div>
            );
          })}
      </div>
      <div>
        <h2>DONELIST</h2>
        {todos
          .filter((todo) => {
            todo.isDone === true;
          })
          .map((todo) => {
            return (
              <div
                style={{
                  border: "1px solid black",
                  margin: "10px",
                }}
                key={todo.id}
              >
                {todo.id} <br />
                {todo.title} <br />
                {todo.body} <br />
                {todo.isDone.toString()} <br />
                <button
                  onClick={() => {
                    dispatch({
                      type: "SWITCH_TODO",
                      payload: todo.id,
                    });
                  }}
                >
                  취소
                </button>
                <button
                  onClick={() => {
                    dispatch({
                      type: "DELETE_TODO",
                      payload: todo.id,
                    });
                  }}
                >
                  삭제
                </button>
              </div>
            );
          })}
      </div>
    </div>
  );
};

export default List;
